#!/usr/bin/env python 3.9# coding:utf-8"""自定义公共方法"""#定义登录认证装饰器import osimport timedef login_auto(func):#func 1，被装饰函数名    from core import src    def decorate():        print("token",src.token)        if src.token is not None:            res=func()#2，如果全局变量为True 这进行调用被装饰函数            return res        else:#3，否则从新运行，run方法            print("未登录，请登录后使用".center(50,"*"))            src.run()    return decoratedef Logging_info(msg):#保存操作日志    from conf import setting    file_name=time.strftime("%Y-%m-%d.out")    file=os.path.join(setting.Path_base,"logs",file_name)    with open(file=file,mode="a+t",encoding="utf8") as f:        f.write(f"{msg}\n")# Logging_info(1)des=[]if len(des):    print(1)